const envName = "env1"
const svcName = "svc1"
const ardID = "0987"

func TestMarketplaceMigrationForAPIKeyRevision(t *testing.T) {
	rev := newRevision(svcName, "api-key-revision")
	rev.ResourceMeta.Metadata.SelfLink = fmt.Sprintf("/management/v1alpha1/environments/azure/apiservicerevisions/%s", rev.Name)

	c := &mockMPMigClient{
		revisions: []*apiv1.ResourceInstance{
			rev,
		},
		instances: []*apiv1.ResourceInstance{
			newInstance(rev.Name, "inst1", "", []string{"api-key"}),
			newInstance(rev.Name, "inst2", "", []string{"api-key"}),
		},
	}

	cfg := &config.CentralConfiguration{
		Environment:      envName,
		APIServerVersion: "v1alpha1",
	}
	cm := cache.NewAgentCacheManager(cfg, false)

	ard := management.NewAccessRequestDefinition("api-key", envName)
	ard.Metadata.ID = "123"
	ri, _ := ard.AsInstance()

	cm.AddAccessRequestDefinition(ri)

	svc := management.NewAPIService(svcName, envName)
	svcRI, _ := svc.AsInstance()

	mig := NewMarketplaceMigration(c, cfg, cm)
	_, err := mig.Migrate(context.Background(), svcRI)
	assert.Nil(t, err)

	assert.Equal(t, 2, c.updateCount)
}

func TestMarketplaceMigrationForOAuthRevision(t *testing.T) {
	rev := newOAuthRev(svcName, "oauth-revision")
	rev.ResourceMeta.Metadata.SelfLink = fmt.Sprintf("/management/v1alpha1/environments/azure/apiservicerevisions/%s", rev.Name)

	c := &mockMPMigClient{
		revisions: []*apiv1.ResourceInstance{
			rev,
		},
		instances: []*apiv1.ResourceInstance{
			newInstance(rev.Name, "inst1", "", []string{provisioning.OAuthPublicKeyCRD, provisioning.OAuthSecretCRD}),
		},
	}
	cfg := &config.CentralConfiguration{
		Environment:      envName,
		APIServerVersion: "v1alpha1",
	}
	cm := cache.NewAgentCacheManager(cfg, false)

	ard := management.NewAccessRequestDefinition("oauth", envName)
	ard.Metadata.ID = "0012"
	ri, _ := ard.AsInstance()

	cm.AddAccessRequestDefinition(ri)

	svc := management.NewAPIService(svcName, envName)
	svcRI, _ := svc.AsInstance()

	mig := NewMarketplaceMigration(c, cfg, cm)
	_, err := mig.Migrate(context.Background(), svcRI)
	assert.Nil(t, err)

	assert.Equal(t, 1, c.updateCount)
	a, _ := cm.GetAccessRequestDefinitionByID(ardID)
	assert.NotNil(t, a)
}

func newInstance(revName, instName, ard string, credentialRequestDefinitions []string) *apiv1.ResourceInstance {
	inst := management.NewAPIServiceInstance(instName, envName)
	inst.Spec = management.ApiServiceInstanceSpec{
		ApiServiceRevision:           revName,
		AccessRequestDefinition:      ard,
		CredentialRequestDefinitions: credentialRequestDefinitions,
		Endpoint: []management.ApiServiceInstanceSpecEndpoint{
			{
				Host:     "lbean004.lab.phx.axway.int",
				Port:     8065,
				Protocol: "https",
				Routing: management.ApiServiceInstanceSpecRouting{
					BasePath: "/api1",
				},
			},
		},
	}
	ri, _ := inst.AsInstance()
	return ri
}

func newRevision(svcName, revName string) *apiv1.ResourceInstance {
	rev := management.NewAPIServiceRevision(revName, envName)
	rev.Spec = management.ApiServiceRevisionSpec{
		ApiService: svcName,
		Definition: management.ApiServiceRevisionSpecDefinition{
			Type:  "oas2",
			Value: "",
		},
	}

	ri, _ := rev.AsInstance()
	return ri
}

func newOAuthRev(svcName, revName string) *apiv1.ResourceInstance {
	rev := management.NewAPIServiceRevision(revName, envName)
	rev.Spec = management.ApiServiceRevisionSpec{
		ApiService: svcName,
		Definition: management.ApiServiceRevisionSpecDefinition{
			Type:  "oas2",
			Value: "",
		},
	}

	ri, _ := rev.AsInstance()
	return ri
}

type mockMPMigClient struct {
	sync.Mutex
	updateCount     int
	createSubCalled bool
	revisions       []*apiv1.ResourceInstance
	instances       []*apiv1.ResourceInstance
}

func (m *mockMPMigClient) ExecuteAPI(_, _ string, _ map[string]string, _ []byte) ([]byte, error) {
	return nil, nil
}

func (m *mockMPMigClient) GetAPIV1ResourceInstances(_ map[string]string, url string) ([]*apiv1.ResourceInstance, error) {
	if strings.Contains(url, "instances") {
		return m.instances, nil
	}
	return m.revisions, nil

}

func (m *mockMPMigClient) Updatev1ResourceInstance(_ string, _ *apiv1.ResourceInstance) (*apiv1.ResourceInstance, error) {
	return nil, nil
}

func (m *mockMPMigClient) CreateSubResourceScoped(_ apiv1.ResourceMeta, _ map[string]interface{}) error {
	return nil
}

func (m *mockMPMigClient) CreateSubResource(_ apiv1.ResourceMeta, _ map[string]interface{}) error {
	m.Lock()
	defer m.Unlock()
	m.createSubCalled = true
	return nil
}

func (m *mockMPMigClient) UpdateResourceInstance(_ apiv1.Interface) (*apiv1.ResourceInstance, error) {
	m.Lock()
	defer m.Unlock()
	m.updateCount = m.updateCount + 1
	return nil, nil
}

func (m *mockMPMigClient) CreateOrUpdateResource(i apiv1.Interface) (*apiv1.ResourceInstance, error) {
	ri, _ := i.AsInstance()
	ri.Metadata.ID = ardID
	return ri, nil
}

func (m *mockMPMigClient) DeleteResourceInstance(ri apiv1.Interface) error {
	return nil
}

func (m *mockMPMigClient) GetResource(url string) (*apiv1.ResourceInstance, error) {
	// may need to revisit this in the future.  Hardcoded the return of the version based on the request definition
	var rev *apiv1.ResourceInstance

	if strings.Contains(url, "api-key-revision") {
		rev = newRevision(svcName, "api-key-revision")
	} else {
		rev = newOAuthRev(svcName, "oauth-revision")
	}

	return rev, nil
}
